/* 
 * Este es un sonido de notificación simple en formato base64
 * Para uso en la aplicación de StartUpConnect
 */

const notificationSound = document.createElement('audio');
notificationSound.src = 'data:audio/mp3;base64,';

function playNotificationSound() {
  notificationSound.play();
}

